import React, { useState } from "react";
import cls from "./event.module.scss";
import cx from "classnames";
import Icon from "../../../../components/icons/icons";

interface EventProps {}

const tablists = [
  { idx: 0, key: "Oldingi" },
  { idx: 1, key: "Hozirgi" },
];

const Event: React.FC<EventProps> = () => {
  const [index, setIndex] = useState(0);
  return (
    <section className={cls.events}>
      <h2 className={cls.title}>Tadbirlar</h2>
      <div className={cls.tablist}>
        {tablists.map(({ idx, key }) => (
          <div
            className={cx(cls["tab-head"], index === idx ? cls.active : null)}
            onClick={() => {
              setIndex(idx);
              console.log(index);
            }}
          >
            {key}
          </div>
        ))}
      </div>
      <div className={cls["tab-content"]} hidden={index !== 0}>
        <div className={cls.card}>
          <div className={cls["card-top"]}>
            <div className={cls.info}>
              <h3>ICT Week</h3>
              <p>Iqtsodiyot vazirligi</p>
            </div>
            <div className={cls.status}></div>
            <div className="card-bottom">
              <div className="info-bottom">
                <Icon name="Calendar" /> <p>11.02.2022 - 17.02.2022</p>
              </div>
              <div className="info-bottom">
                <Icon name="location" />{" "}
                <p>Toshkent viloyati, UsmonNosir koch. 15-uy</p>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className={cls["tab-content"]} hidden={index !== 1}>
        second
      </div>
    </section>
  );
};

export default Event;
