import React, { useState } from "react";
import cls from "./event.module.scss";
import cx from "classnames";

interface EventProps {}

const Event: React.FC<EventProps> = () => {
  const [index, setIndex] = useState(0);
  return (
    <section className={cls.events}>
      <h2 className={cls.title}>Tadbirlar</h2>
      <div className={cls.tablist}>
        <div
          className={cx(cls["tab-head"], index === 0 ? cls.active : null)}
          onClick={() => setIndex(0)}
        >
          Oldingi
        </div>
        <div className={cls["tab-head"]} onClick={() => setIndex(1)}>
          Hozirgi
        </div>
      </div>
      <div className={cls["tab-content"]} hidden={index !== 0}>
        first
      </div>
      <div className={cls["tab-content"]} hidden={index !== 1}>
        second
      </div>
      <div className={cls["tab-content"]} hidden={index !== 2}>
        third
      </div>
    </section>
  );
};

export default Event;
